import React, { useState } from 'react';

import '../../asset/css/ExpenseFilter.css';

const ExpensesFilter = (props) => {

    const [userDateInput, setUserDateInput] = useState('');

    const dateFilterHandler = (event) => {
        console.log(event.target.value);
        setUserDateInput(event.target.value);
        props.onAddFilteredDate(userDateInput);
    };

    return (
        <div className='expenses-filter'>
            <div className='expenses-filter__control'>
                <label>Filter by year</label>
                <select onChange={dateFilterHandler}>
                    <option value='2022'>2022</option>
                    <option value='2021'>2021</option>
                    <option value='2020'>2020</option>
                    <option value='2019'>2019</option>
                </select>
            </div>
        </div>
    );
};

export default ExpensesFilter;
